export default {
  data: function data() {
    return {
      list: [],
      count: 0,
      allChecked: false
    };
  },
  props: ['goToCart'],
  created: function created() {
    this.loadMore();
  },
  methods: {
    //全选功能
    handleChecked: function handleChecked(item) {
      if (this.allChecked == false) {
        for (var i = 0; i < this.list.length; i++) {
          var item = this.list[i];
          item.cb = true;
        }
      } else {
        for (var i = 0; i < this.list.length; i++) {
          var item = this.list[i];
          item.cb = false;
        }
      }

      this.allChecked = !this.allChecked;
    },
    delAll: function delAll() {
      var _this = this;

      this.$messagebox.confirm("是否删除商品").then(function (res) {
        // 同意
        // 发送ajax服务器端程序
        var url = "delAll";

        _this.axios.get(url).then(function (res) {
          if (res.data.code > 0) {
            _this.$toast("删除成功");

            _this.$store.commit('clear'); // 返回服务器返回内容
            //重新调用loadMore最新购物列表查询


            _this.loadMore();
          }
        });
      }).catch(function (err) {});
    },
    handler: function handler(e) {
      var i = e.target.dataset.i;
      this.list[i].count += parseInt(e.target.dataset.n);

      if (e.target.dataset.n == '1') {
        this.$store.commit('increment');
      } else {
        this.$store.commit('reduce');
      }
    },
    loadMore: function loadMore() {
      var _this2 = this;

      var url = "carts";
      this.axios.get(url).then(function (res) {
        console.log(res);

        if (res.data.code == -1) {
          _this2.$messagebox("消息", "请登录").then(function (res) {
            _this2.$router.push("/Login");

            return;
          });
        } else {
          var rows = res.data.data;
          console.log(rows);
          var _iteratorNormalCompletion = true;
          var _didIteratorError = false;
          var _iteratorError = undefined;

          try {
            for (var _iterator = rows[Symbol.iterator](), _step; !(_iteratorNormalCompletion = (_step = _iterator.next()).done); _iteratorNormalCompletion = true) {
              var item = _step.value;
              item.cb = false;
            }
          } catch (err) {
            _didIteratorError = true;
            _iteratorError = err;
          } finally {
            try {
              if (!_iteratorNormalCompletion && _iterator.return != null) {
                _iterator.return();
              }
            } finally {
              if (_didIteratorError) {
                throw _iteratorError;
              }
            }
          }

          _this2.list = rows;
        }
      });
    },
    delItem: function delItem(e, i) {
      var _this3 = this;

      // 功能：删除购物车中指定商品
      // 获取购物车id
      var id = e.target.dataset.id; // 交互提示用户是否删除指定商品

      this.$messagebox.confirm("是否删除商品").then(function (res) {
        // 同意
        // 发送ajax服务器端程序
        var url = "delItem";
        var obj = {
          id: id
        };

        _this3.axios.get(url, {
          params: obj
        }).then(function (res) {
          if (res.data.code > 0) {
            _this3.$toast("删除成功"); // 返回服务器返回内容
            //重新调用loadMore最新购物列表查询


            var n = _this3.list[i].count;

            _this3.$store.commit('reduce', n);

            _this3.loadMore();
          }
        });
      }).catch(function (err) {});
    },
    delItems: function delItems() {
      var _this4 = this;

      this.$messagebox.confirm("是否删除商品?").then(function (res) {
        // var n=0;
        var str = "";
        var _iteratorNormalCompletion2 = true;
        var _didIteratorError2 = false;
        var _iteratorError2 = undefined;

        try {
          for (var _iterator2 = _this4.list[Symbol.iterator](), _step2; !(_iteratorNormalCompletion2 = (_step2 = _iterator2.next()).done); _iteratorNormalCompletion2 = true) {
            var item = _step2.value;

            if (item.cb) {
              str += item.id + ","; // n+=item.count;
              // return n;
            }
          } //console.log(n);

        } catch (err) {
          _didIteratorError2 = true;
          _iteratorError2 = err;
        } finally {
          try {
            if (!_iteratorNormalCompletion2 && _iterator2.return != null) {
              _iterator2.return();
            }
          } finally {
            if (_didIteratorError2) {
              throw _iteratorError2;
            }
          }
        }

        str = str.substring(0, str.length - 1);

        if (str == "") {
          _this4.$toast("请选择需要删除的商品");

          return;
        }

        var url = "delItems";
        var ids = {
          ids: str
        };

        _this4.axios.get(url, {
          params: ids
        }).then(function (res) {
          if (res.data.code == -1) {
            _this4.$toast("删除失败");
          } else {
            _this4.$toast("删除成功"); // this.$store.commit('reduce',n);


            _this4.loadMore();
          }
        });
      }).catch(function (err) {
        console.log(err);
      });
    }
  },
  computed: {
    total: function total() {
      return this.list.filter(function (item) {
        return item.cb;
      }).reduce(function (prev, item) {
        return prev + item.price * item.count;
      }, 0);
    }
  },
  watch: {
    goToCart: function goToCart() {
      this.loadMore();
    }
  }
};
